<js>
res=result
//java.log(res.length)
for (i=0; i<res.length; i++) {
    java.setContent(res[i])
    var scriptid = new String(java.getString("tag.script@id"))
    //java.log(scriptid)
    if (scriptid == "chapters-json") {
        var chapterJson= new String(java.getString("tag.script.chapters-json@all"));
        var chapterJsonString=chapterJson.replace(/<script.*?>|<\/script>/gi, '');
        //java.log(chapterJsonString);
    }else if (scriptid == "f-chapters-json"){
        var fatherJson =  new String(java.getString("tag.script.f-chapters-json@all"))
        var fatherJsonString=fatherJson.replace(/<script.*?>|<\/script>/gi, '');
        //java.log(fatherJsonString);
    }    
}
if (chapterJsonString != "" && fatherJsonString != "") {
    var fatherChapterArr = JSON.parse(fatherJsonString);
    var novelChapterArr = JSON.parse(chapterJsonString);
    var page='';
    for (let i = 0; i < fatherChapterArr.length; i++){
        page+=`<li><ul class="episode-ul">`;
        page+=`<a><li class="episode-box"><h3>${fatherChapterArr[i]['title'] ? fatherChapterArr[i]['title'] : ''}&nbsp;${fatherChapterArr[i]['describe'] ? fatherChapterArr[i]['describe'] : ''}</h3></li></a>`;

       for (let j = 0; j < novelChapterArr.length; j++){
            var episode=novelChapterArr[j];
            if(episode['parent_id']==fatherChapterArr[i]['id']){

                let payRecord = '';
                if (episode['cost'] > 0){
                    payRecord=episode['cost']+'G';
                }
                                let updateTime=episode['episode_update_time'] ? 1 : 0;
                if (updateTime){
                    updateTime=`<span title="创建时间：${episode['episode_create_time']}；更新时间：${episode['episode_update_time']}">${episode['episode_create_time'].substr(2, 14)}</span>&nbsp;`;
                    if(episode['episode_create_time'] == episode['episode_update_time']){
                        updateTime+='<small">(<u>更新</u>)</small>';
                    }else{
                        updateTime+=`<small title="更新时间：${episode['episode_update_time']}">(<u>更新</u>)</small>`;
                    }
                }else if(updateTime){
                    updateTime=`<span>${episode['episode_update_time'].substr(2, 8)}</span>`;
                }
                page+=`<a href="/admin/novelReading?cid=${episode['id']}">
                <li class="episode-box">
                <span>${episode['title'] ? episode['title'] : ''}&nbsp;${episode['describe'] ? episode['describe'] : ''}</span>
                </li></a>`;
            }
        }
        page+='</ul></li>';
    }
}
